/**
 * Flysquawk Integrated Website - Main JavaScript
 * Handles navigation, sliders, animations, form submission, and scroll effects
 */

(function() {
    'use strict';

    // =================================================================
    // ELEMENT SDK INTEGRATION
    // =================================================================

    const defaultConfig = {
        hero_headline: "Rejuvenate Your Industrial Power",
        hero_subtext: "Malaysia's trusted MRO partner restoring turbines, compressors, and pumps.",
        cta_button_text: "Discover Our Capabilities",
        tagline: "Preserving Power. Protecting Capital.",
        phone_number: "+60 19 986 9487",
        email_address: "enquiry@flysquawk.com.my"
    };

    async function onConfigChange(config) {
        const heroHeadline = document.getElementById('hero-headline');
        const heroSubtext = document.getElementById('hero-subtext');
        const tagline = document.getElementById('tagline');

        if (heroHeadline) heroHeadline.textContent = config.hero_headline || defaultConfig.hero_headline;
        if (heroSubtext) heroSubtext.textContent = config.hero_subtext || defaultConfig.hero_subtext;
        if (tagline) tagline.textContent = config.tagline || defaultConfig.tagline;

        const ctaButtons = [
            document.getElementById('cta-button-1'),
            document.getElementById('cta-button-2'),
            document.getElementById('cta-button-3')
        ];
        ctaButtons.forEach(btn => {
            if (btn) btn.textContent = config.cta_button_text || defaultConfig.cta_button_text;
        });

        const phoneElements = document.querySelectorAll('.phone-number');
        phoneElements.forEach(el => el.textContent = config.phone_number || defaultConfig.phone_number);

        const emailElements = document.querySelectorAll('.email-address');
        emailElements.forEach(el => el.textContent = config.email_address || defaultConfig.email_address);
    }

    function mapToCapabilities(config) {
        return {
            recolorables: [],
            borderables: [],
            fontEditable: undefined,
            fontSizeable: undefined
        };
    }

    function mapToEditPanelValues(config) {
        return new Map([
            ["hero_headline", config.hero_headline || defaultConfig.hero_headline],
            ["hero_subtext", config.hero_subtext || defaultConfig.hero_subtext],
            ["cta_button_text", config.cta_button_text || defaultConfig.cta_button_text],
            ["tagline", config.tagline || defaultConfig.tagline],
            ["phone_number", config.phone_number || defaultConfig.phone_number],
            ["email_address", config.email_address || defaultConfig.email_address]
        ]);
    }

    // Initialize Element SDK if available
    if (window.elementSdk) {
        window.elementSdk.init({
            defaultConfig,
            onConfigChange,
            mapToCapabilities,
            mapToEditPanelValues
        });
    }

    // =================================================================
    // NAVIGATION SYSTEM
    // =================================================================

    /**
     * Initialize page navigation and active states
     */
    function initNavigation() {
        let currentPage = 'home';

        // Main page switching function
        window.showPage = function(pageName) {
            // Hide all pages
            const allPages = document.querySelectorAll('.page');
            allPages.forEach(page => {
                page.style.display = 'none';
            });

            // Show selected page
            const selectedPage = document.getElementById(`page-${pageName}`);
            if (selectedPage) {
                selectedPage.style.display = 'block';
                currentPage = pageName;

                // Update active nav link
                const navLinks = document.querySelectorAll('.nav-links a');
                navLinks.forEach(link => {
                    link.classList.remove('active');
                });

                const activeLink = document.querySelector(`.nav-links a[data-page="${pageName}"]`);
                if (activeLink) {
                    activeLink.classList.add('active');
                }

                // Scroll to top
                window.scrollTo({ top: 0, behavior: 'smooth' });

                // Trigger animations for visible elements
                setTimeout(() => {
                    const visibleElements = selectedPage.querySelectorAll('.fade-in');
                    visibleElements.forEach(el => {
                        el.classList.add('visible');
                    });
                }, 100);
            }
        };

        // Navigation link click handlers
        const navLinks = document.querySelectorAll('.nav-links a');
        navLinks.forEach(link => {
            link.addEventListener('click', function(e) {
                e.preventDefault();
                const pageName = this.getAttribute('data-page');
                if (pageName) {
                    window.showPage(pageName);
                }
            });
        });

        // Mobile menu link click handlers
        const mobileMenuLinks = document.querySelectorAll('.mobile-menu a');
        mobileMenuLinks.forEach(link => {
            link.addEventListener('click', function(e) {
                e.preventDefault();
                const pageName = this.getAttribute('data-page');
                if (pageName) {
                    window.showPage(pageName);
                    closeMobileMenu();
                }
            });
        });

        // CTA button handlers
        const ctaButton1 = document.getElementById('cta-button-1');
        const ctaButton2 = document.getElementById('cta-button-2');
        const ctaButton3 = document.getElementById('cta-button-3');

        if (ctaButton1) {
            ctaButton1.addEventListener('click', function(e) {
                e.preventDefault();
                window.showPage('services');
            });
        }

        if (ctaButton2) {
            ctaButton2.addEventListener('click', function(e) {
                e.preventDefault();
                window.showPage('projects');
            });
        }

        if (ctaButton3) {
            ctaButton3.addEventListener('click', function(e) {
                e.preventDefault();
                window.showPage('contact');
            });
        }
    }

    // =================================================================
    // MOBILE MENU TOGGLE
    // =================================================================

    /**
     * Initialize mobile menu functionality
     */
    function initMobileMenu() {
        const mobileMenuToggle = document.querySelector('.mobile-menu-toggle');
        const mobileMenu = document.querySelector('.mobile-menu');

        if (!mobileMenuToggle || !mobileMenu) return;

        // Toggle menu open/close
        mobileMenuToggle.addEventListener('click', function() {
            mobileMenuToggle.classList.toggle('active');
            mobileMenu.classList.toggle('active');
        });

        // Close menu when clicking outside
        document.addEventListener('click', function(event) {
            const isClickInsideMenu = mobileMenu.contains(event.target);
            const isClickOnToggle = mobileMenuToggle.contains(event.target);

            if (!isClickInsideMenu && !isClickOnToggle && mobileMenu.classList.contains('active')) {
                closeMobileMenu();
            }
        });
    }

    /**
     * Close mobile menu
     */
    function closeMobileMenu() {
        const mobileMenuToggle = document.querySelector('.mobile-menu-toggle');
        const mobileMenu = document.querySelector('.mobile-menu');

        if (mobileMenuToggle) mobileMenuToggle.classList.remove('active');
        if (mobileMenu) mobileMenu.classList.remove('active');
    }

    // =================================================================
    // SLIDER FUNCTIONALITY
    // =================================================================

    /**
     * Initialize all slider components
     * Handles capability sliders, gas turbine slider, project slider, and industries slider
     */
    function initSliders() {
        // --- Capability Sliders ---
        const capabilitySliders = document.querySelectorAll('.capability-slider');

        capabilitySliders.forEach(slider => {
            const container = slider.querySelector('.slider-container');
            const slides = slider.querySelectorAll('.slide-card');
            const prevBtn = slider.querySelector('.slider-prev');
            const nextBtn = slider.querySelector('.slider-next');

            if (!container || !slides.length || !prevBtn || !nextBtn) return;

            let currentSlide = 0;
            const slideWidth = 320;
            const visibleSlides = Math.floor(slider.offsetWidth / slideWidth);
            const maxSlide = Math.max(0, slides.length - visibleSlides);

            const updateSlider = () => {
                const translateX = -currentSlide * slideWidth;
                container.style.transform = `translateX(${translateX}px)`;
            };

            const nextSlide = () => {
                currentSlide = Math.min(currentSlide + 1, maxSlide);
                updateSlider();
            };

            const prevSlide = () => {
                currentSlide = Math.max(currentSlide - 1, 0);
                updateSlider();
            };

            nextBtn.addEventListener('click', nextSlide);
            prevBtn.addEventListener('click', prevSlide);

            // Auto-advance slides every 8 seconds
            setInterval(() => {
                if (currentSlide >= maxSlide) {
                    currentSlide = 0;
                } else {
                    currentSlide++;
                }
                updateSlider();
            }, 8000);

            // Touch/swipe support for mobile
            let startX = 0;
            let endX = 0;

            slider.addEventListener('touchstart', (e) => {
                startX = e.touches[0].clientX;
            });

            slider.addEventListener('touchend', (e) => {
                endX = e.changedTouches[0].clientX;
                const diff = startX - endX;

                if (Math.abs(diff) > 50) {
                    if (diff > 0) {
                        nextSlide();
                    } else {
                        prevSlide();
                    }
                }
            });
        });

        // --- Gas Turbine Slider ---
        const gasTurbineSlider = document.querySelector('.gas-turbine-slider');
        if (gasTurbineSlider) {
            const container = gasTurbineSlider.querySelector('.gas-turbine-container');
            const slides = gasTurbineSlider.querySelectorAll('.gas-turbine-slide');
            const prevBtn = gasTurbineSlider.querySelector('.gas-turbine-prev');
            const nextBtn = gasTurbineSlider.querySelector('.gas-turbine-next');

            if (container && slides.length && prevBtn && nextBtn) {
                let currentSlide = 0;
                const maxSlide = slides.length - 1;

                const updateGasTurbineSlider = () => {
                    const translateX = -currentSlide * 100;
                    container.style.transform = `translateX(${translateX}%)`;
                };

                const nextGasTurbineSlide = () => {
                    currentSlide = currentSlide >= maxSlide ? 0 : currentSlide + 1;
                    updateGasTurbineSlider();
                };

                const prevGasTurbineSlide = () => {
                    currentSlide = currentSlide <= 0 ? maxSlide : currentSlide - 1;
                    updateGasTurbineSlider();
                };

                nextBtn.addEventListener('click', nextGasTurbineSlide);
                prevBtn.addEventListener('click', prevGasTurbineSlide);

                // Auto-advance slides every 10 seconds
                setInterval(() => {
                    nextGasTurbineSlide();
                }, 10000);

                // Touch/swipe support for mobile
                let startX = 0;
                let endX = 0;

                gasTurbineSlider.addEventListener('touchstart', (e) => {
                    startX = e.touches[0].clientX;
                });

                gasTurbineSlider.addEventListener('touchend', (e) => {
                    endX = e.changedTouches[0].clientX;
                    const diff = startX - endX;

                    if (Math.abs(diff) > 50) {
                        if (diff > 0) {
                            nextGasTurbineSlide();
                        } else {
                            prevGasTurbineSlide();
                        }
                    }
                });
            }
        }

        // --- Project Slider ---
        const projectSliders = document.querySelectorAll('.project-slider');

        projectSliders.forEach(slider => {
            const container = slider.querySelector('.project-slider-container');
            const slides = slider.querySelectorAll('.project-slide-card');
            const prevBtn = slider.querySelector('.project-slider-prev');
            const nextBtn = slider.querySelector('.project-slider-next');

            if (!container || !slides.length || !prevBtn || !nextBtn) return;

            let currentSlide = 0;
            const maxSlide = slides.length - 1;

            const updateProjectSlider = () => {
                const translateX = -currentSlide * 100;
                container.style.transform = `translateX(${translateX}%)`;
            };

            const nextProjectSlide = () => {
                currentSlide = currentSlide >= maxSlide ? 0 : currentSlide + 1;
                updateProjectSlider();
            };

            const prevProjectSlide = () => {
                currentSlide = currentSlide <= 0 ? maxSlide : currentSlide - 1;
                updateProjectSlider();
            };

            nextBtn.addEventListener('click', nextProjectSlide);
            prevBtn.addEventListener('click', prevProjectSlide);

            // Auto-advance slides every 12 seconds
            setInterval(() => {
                nextProjectSlide();
            }, 12000);

            // Touch/swipe support for mobile
            let startX = 0;
            let endX = 0;

            slider.addEventListener('touchstart', (e) => {
                startX = e.touches[0].clientX;
            });

            slider.addEventListener('touchend', (e) => {
                endX = e.changedTouches[0].clientX;
                const diff = startX - endX;

                if (Math.abs(diff) > 50) {
                    if (diff > 0) {
                        nextProjectSlide();
                    } else {
                        prevProjectSlide();
                    }
                }
            });
        });

        // --- Industries Slider ---
        const industriesSlider = document.querySelector('.industries-slider');
        if (industriesSlider) {
            const container = industriesSlider.querySelector('.industries-slider-container');
            const slides = industriesSlider.querySelectorAll('.industry-slide-card');
            const prevBtn = industriesSlider.querySelector('.industries-slider-prev');
            const nextBtn = industriesSlider.querySelector('.industries-slider-next');

            if (container && slides.length && prevBtn && nextBtn) {
                let currentSlide = 0;
                const slideWidth = 370;
                const visibleSlides = Math.floor(industriesSlider.offsetWidth / slideWidth);
                const maxSlide = Math.max(0, slides.length - visibleSlides);

                const updateIndustriesSlider = () => {
                    const translateX = -currentSlide * slideWidth;
                    container.style.transform = `translateX(${translateX}px)`;
                };

                const nextIndustriesSlide = () => {
                    currentSlide = Math.min(currentSlide + 1, maxSlide);
                    updateIndustriesSlider();
                };

                const prevIndustriesSlide = () => {
                    currentSlide = Math.max(currentSlide - 1, 0);
                    updateIndustriesSlider();
                };

                nextBtn.addEventListener('click', nextIndustriesSlide);
                prevBtn.addEventListener('click', prevIndustriesSlide);

                // Auto-advance slides every 6 seconds
                setInterval(() => {
                    if (currentSlide >= maxSlide) {
                        currentSlide = 0;
                    } else {
                        currentSlide++;
                    }
                    updateIndustriesSlider();
                }, 6000);

                // Touch/swipe support for mobile
                let startX = 0;
                let endX = 0;

                industriesSlider.addEventListener('touchstart', (e) => {
                    startX = e.touches[0].clientX;
                });

                industriesSlider.addEventListener('touchend', (e) => {
                    endX = e.changedTouches[0].clientX;
                    const diff = startX - endX;

                    if (Math.abs(diff) > 50) {
                        if (diff > 0) {
                            nextIndustriesSlide();
                        } else {
                            prevIndustriesSlide();
                        }
                    }
                });
            }
        }
    }

    // =================================================================
    // SCROLL ANIMATIONS
    // =================================================================

    /**
     * Initialize fade-in animations using IntersectionObserver
     */
    function initAnimations() {
        const observerOptions = {
            threshold: 0.1,
            rootMargin: '0px 0px -50px 0px'
        };

        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('visible');
                }
            });
        }, observerOptions);

        const fadeInElements = document.querySelectorAll('.fade-in');
        fadeInElements.forEach(el => {
            observer.observe(el);
        });
    }

    // =================================================================
    // FORM HANDLING
    // =================================================================

    /**
     * Initialize contact form submission with success message
     */
    function initFormHandling() {
        const enquiryForm = document.getElementById('enquiry-form');

        if (!enquiryForm) return;

        enquiryForm.addEventListener('submit', function(e) {
            e.preventDefault();

            const formData = {
                name: document.getElementById('name')?.value,
                email: document.getElementById('email')?.value,
                company: document.getElementById('company')?.value,
                message: document.getElementById('message')?.value
            };

            // Create success message
            const successMessage = document.createElement('div');
            successMessage.style.cssText = `
                position: fixed;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);
                background: linear-gradient(135deg, #2E005C, #4B1D8C);
                color: white;
                padding: 2rem 3rem;
                border-radius: 12px;
                box-shadow: 0 20px 60px rgba(46, 0, 92, 0.4);
                z-index: 10000;
                text-align: center;
                font-family: 'Montserrat', sans-serif;
            `;
            successMessage.innerHTML = `
                <div style="font-size: 3rem; margin-bottom: 1rem;">âœ“</div>
                <h3 style="margin-bottom: 0.5rem; font-size: 1.5rem;">Thank You!</h3>
                <p style="opacity: 0.9;">Your enquiry has been received. We'll contact you shortly.</p>
            `;
            document.body.appendChild(successMessage);

            // Reset form
            this.reset();

            // Remove message after 3 seconds
            setTimeout(() => {
                successMessage.style.opacity = '0';
                successMessage.style.transition = 'opacity 0.3s ease';
                setTimeout(() => successMessage.remove(), 300);
            }, 3000);
        });
    }

    // =================================================================
    // SCROLL EFFECTS
    // =================================================================

    /**
     * Initialize navbar scroll effects
     */
    function initScrollEffects() {
        let lastScroll = 0;

        window.addEventListener('scroll', () => {
            const nav = document.querySelector('nav');
            if (!nav) return;

            const currentScroll = window.pageYOffset;

            if (currentScroll > 100) {
                nav.style.boxShadow = '0 2px 30px rgba(46, 0, 92, 0.15)';
            } else {
                nav.style.boxShadow = '0 2px 20px rgba(46, 0, 92, 0.08)';
            }

            lastScroll = currentScroll;
        });
    }

    // =================================================================
    // INITIALIZATION
    // =================================================================

    /**
     * Initialize all components when DOM is ready
     */
    function init() {
        initNavigation();
        initMobileMenu();
        initSliders();
        initAnimations();
        initFormHandling();
        initScrollEffects();
    }

    // Run initialization when DOM is fully loaded
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', init);
    } else {
        init();
    }

})();

assets/styles.css.content:
/* ========================================
   CSS VARIABLES
   ======================================== */
:root {
    /* Brand Colors - Purple/Blue Theme */
    --color-primary: #2B0057;
    --color-primary-light: #5A00A0;
    --color-secondary: #A8E0FF;
    --color-accent-orange: #F7931E;
    --color-accent-gold: #C49A6C;

    /* Additional Theme Colors */
    --color-navy: #0A2342;
    --color-navy-light: #1a3a5c;
    --color-purple-alt: #2E005C;
    --color-purple-mid: #4B1D8C;
    --color-purple-light: #3D2C8D;
    --color-blue-light: #7FD3F4;

    /* Neutral Colors */
    --color-text: #333333;
    --color-text-light: #666666;
    --color-white: #FFFFFF;
    --color-bg: #F4F4F4;
    --color-bg-light: #EAEAEA;
    --color-bg-card: #f8f9fa;

    /* Gradients */
    --gradient-primary: linear-gradient(135deg, #2B0057 0%, #5A00A0 50%, #A8E0FF 100%);
    --gradient-hero: linear-gradient(180deg, #2B0057 0%, #5A00A0 100%);
    --gradient-cta: linear-gradient(135deg, #5A00A0, #A8E0FF);
    --gradient-service: linear-gradient(135deg, #2B0057 0%, #5A00A0 100%);
    --gradient-service-overlay: linear-gradient(135deg, transparent 0%, rgba(168, 224, 255, 0.2) 100%);
    --gradient-accent: linear-gradient(135deg, #C49A6C, #F7931E);
    --gradient-navy: linear-gradient(135deg, #0A2342, #F7931E);
    --gradient-bg: linear-gradient(180deg, #F4F4F4 0%, white 100%);
    --gradient-industries: linear-gradient(180deg, #EAEAEA 0%, white 100%);
    --gradient-purple-alt: linear-gradient(135deg, #2E005C, #4B1D8C);
    --gradient-blue: linear-gradient(135deg, #7FD3F4, #2E005C);
    --gradient-services-page: linear-gradient(180deg, #0A2342 0%, #1a3a5c 100%);

    /* Spacing */
    --spacing-xs: 0.5rem;
    --spacing-sm: 1rem;
    --spacing-md: 1.5rem;
    --spacing-lg: 2rem;
    --spacing-xl: 2.5rem;
    --spacing-2xl: 3rem;
    --spacing-3xl: 4rem;
    --spacing-4xl: 5rem;

    /* Font Families */
    --font-primary: 'Open Sans', sans-serif;
    --font-heading: 'Montserrat', sans-serif;
    --font-logo: 'Orbitron', 'Eurostile', 'Montserrat', sans-serif;

    /* Font Sizes */
    --font-size-xs: 0.85rem;
    --font-size-sm: 0.9rem;
    --font-size-base: 0.95rem;
    --font-size-md: 1rem;
    --font-size-lg: 1.05rem;
    --font-size-xl: 1.1rem;
    --font-size-2xl: 1.3rem;
    --font-size-3xl: 1.4rem;
    --font-size-4xl: 1.5rem;
    --font-size-5xl: 1.8rem;
    --font-size-6xl: 2rem;
    --font-size-7xl: 2.5rem;
    --font-size-8xl: 3rem;
    --font-size-9xl: 3.5rem;

    /* Border Radius */
    --radius-sm: 8px;
    --radius-md: 12px;
    --radius-lg: 16px;
    --radius-xl: 20px;
    --radius-full: 50px;
    --radius-circle: 50%;

    /* Shadows */
    --shadow-sm: 0 2px 20px rgba(43, 0, 87, 0.08);
    --shadow-md: 0 5px 20px rgba(43, 0, 87, 0.1);
    --shadow-lg: 0 10px 40px rgba(43, 0, 87, 0.1);
    --shadow-xl: 0 15px 50px rgba(0, 0, 0, 0.2);
    --shadow-card: 0 8px 25px rgba(10, 35, 66, 0.1);
    --shadow-card-alt: 0 10px 40px rgba(61, 44, 141, 0.1);
    --shadow-button: 0 4px 15px rgba(0, 0, 0, 0.3);
    --shadow-cta: 0 10px 30px rgba(90, 0, 160, 0.3);
    --shadow-cta-hover: 0 15px 40px rgba(168, 224, 255, 0.5);
    --shadow-service: 0 20px 50px rgba(168, 224, 255, 0.4);
    --shadow-navy: 0 10px 30px rgba(10, 35, 66, 0.3);
    --shadow-orange: 0 15px 40px rgba(247, 147, 30, 0.6);
    --shadow-industry: 0 20px 60px rgba(10, 35, 66, 0.2);

    /* Container Widths */
    --container-sm: 1000px;
    --container-md: 1200px;
    --container-lg: 1400px;

    /* Transitions */
    --transition-base: all 0.3s ease;
    --transition-fast: all 0.2s ease;
    --transition-slow: all 0.4s ease;
    --transition-transform: transform 0.4s ease;
}

/* ========================================
   BASE / RESET STYLES
   ======================================== */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html,
body {
    height: 100%;
}

body {
    font-family: var(--font-primary);
    color: var(--color-text);
    background: var(--color-bg);
    overflow-x: hidden;
    box-sizing: border-box;
}

/* ========================================
   LAYOUT STYLES
   ======================================== */
.page {
    min-height: 100vh;
}

.container {
    max-width: var(--container-lg);
    margin: 0 auto;
    padding: 0 5%;
}

.container-md {
    max-width: var(--container-md);
    margin: 0 auto;
    padding: 0 5%;
}

.container-sm {
    max-width: var(--container-sm);
    margin: 0 auto;
    padding: 0 var(--spacing-lg);
}

/* ========================================
   NAVIGATION
   ======================================== */
nav {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background: rgba(255, 255, 255, 0.98);
    backdrop-filter: blur(10px);
    box-shadow: var(--shadow-sm);
    z-index: 1000;
    padding: var(--spacing-sm) 5%;
}

.nav-container {
    max-width: var(--container-lg);
    margin: 0 auto;
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: var(--spacing-3xl);
}

.logo-section {
    display: flex;
    align-items: center;
    gap: var(--spacing-lg);
}

.logo {
    display: flex;
    align-items: center;
    cursor: pointer;
    text-decoration: none;
}

.logo-img {
    height: 50px;
    width: auto;
    object-fit: contain;
    transition: var(--transition-base);
    mix-blend-mode: lighten;
}

.logo:hover .logo-img {
    transform: scale(1.05);
}

.logo-text {
    font-family: var(--font-logo);
    font-weight: 800;
    font-size: var(--font-size-4xl);
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.nav-links {
    display: flex;
    gap: var(--spacing-xl);
    list-style: none;
}

.nav-links a {
    text-decoration: none;
    color: var(--color-primary);
    font-weight: 600;
    font-size: var(--font-size-base);
    transition: var(--transition-base);
    position: relative;
}

.nav-links a:hover {
    color: var(--color-primary-light);
}

.nav-links a::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, #5A00A0, #A8E0FF);
    transition: var(--transition-base);
}

.nav-links a:hover::after {
    width: 100%;
}

.nav-links a.active {
    color: var(--color-primary-light);
}

.nav-links a.active::after {
    width: 100%;
}

/* Mobile Menu Toggle (Hamburger) */
.mobile-menu-toggle {
    display: none;
    flex-direction: column;
    justify-content: space-around;
    width: 30px;
    height: 25px;
    background: transparent;
    border: none;
    cursor: pointer;
    padding: 0;
    z-index: 1001;
}

.mobile-menu-toggle span {
    width: 30px;
    height: 3px;
    background: var(--color-primary);
    border-radius: 3px;
    transition: var(--transition-base);
    transform-origin: center;
}

.mobile-menu-toggle.active span:nth-child(1) {
    transform: translateY(8px) rotate(45deg);
}

.mobile-menu-toggle.active span:nth-child(2) {
    opacity: 0;
}

.mobile-menu-toggle.active span:nth-child(3) {
    transform: translateY(-8px) rotate(-45deg);
}

/* Mobile Menu Dropdown */
.mobile-menu {
    display: none;
    position: fixed;
    top: 70px;
    left: 0;
    right: 0;
    background: rgba(255, 255, 255, 0.98);
    backdrop-filter: blur(10px);
    padding: var(--spacing-lg) 5%;
    box-shadow: var(--shadow-md);
    z-index: 999;
    flex-direction: column;
    gap: var(--spacing-sm);
    max-height: 0;
    overflow: hidden;
    opacity: 0;
    transition: all 0.3s ease;
}

.mobile-menu.active {
    display: flex;
    max-height: 400px;
    opacity: 1;
}

.mobile-menu a {
    text-decoration: none;
    color: var(--color-primary);
    font-weight: 600;
    font-size: var(--font-size-lg);
    padding: var(--spacing-sm) var(--spacing-md);
    border-radius: var(--radius-sm);
    transition: var(--transition-base);
}

.mobile-menu a:hover {
    background: var(--gradient-primary);
    color: white;
}

/* ========================================
   HERO SECTION
   ======================================== */
.hero {
    position: relative;
    height: 100%;
    min-height: 600px;
    background: var(--gradient-hero);
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    margin-top: 80px;
}

.hero-video {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    z-index: 0;
}

.hero-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(43, 0, 87, 0.6);
    z-index: 1;
}

.hero-content {
    position: relative;
    z-index: 2;
    text-align: center;
    color: var(--color-white);
    max-width: 900px;
    padding: var(--spacing-lg);
}

.hero-content h1 {
    font-family: var(--font-heading);
    font-size: var(--font-size-9xl);
    font-weight: 800;
    text-transform: uppercase;
    letter-spacing: 2px;
    margin-bottom: var(--spacing-md);
    line-height: 1.2;
}

.hero-content p {
    font-size: var(--font-size-2xl);
    margin-bottom: var(--spacing-xl);
    font-weight: 300;
    line-height: 1.6;
}

.tagline {
    font-size: var(--font-size-xl);
    font-style: italic;
    margin-bottom: var(--spacing-2xl);
    opacity: 0.9;
}

.cta-button {
    display: inline-block;
    padding: 1.2rem 3rem;
    background: var(--gradient-cta);
    color: var(--color-primary);
    text-decoration: none;
    font-weight: 700;
    font-size: var(--font-size-xl);
    border-radius: var(--radius-full);
    transition: var(--transition-base);
    box-shadow: var(--shadow-cta);
}

.cta-button:hover {
    background: var(--color-white);
    color: var(--color-primary);
    transform: translateY(-3px);
    box-shadow: var(--shadow-cta-hover);
}

/* ========================================
   SECTION STYLES
   ======================================== */
.section-title {
    font-family: var(--font-heading);
    font-size: var(--font-size-7xl);
    font-weight: 700;
    text-align: center;
    color: var(--color-primary);
    margin-bottom: var(--spacing-2xl);
    text-transform: uppercase;
    letter-spacing: 1px;
}

/* ========================================
   HIGHLIGHTS SECTION
   ======================================== */
.highlights {
    padding: var(--spacing-4xl) 5%;
    background: var(--color-bg);
}

.highlights-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: var(--spacing-lg);
    max-width: var(--container-lg);
    margin: 0 auto;
}

.highlight-card {
    background: var(--color-white);
    border: 2px solid var(--color-primary-light);
    border-radius: var(--radius-md);
    padding: var(--spacing-xl) var(--spacing-lg);
    text-align: center;
    transition: var(--transition-base);
    cursor: pointer;
}

.highlight-card:hover {
    border-color: var(--color-secondary);
    box-shadow: 0 10px 40px rgba(168, 224, 255, 0.2);
    transform: translateY(-5px);
}

.highlight-icon {
    font-size: var(--font-size-8xl);
    margin-bottom: var(--spacing-md);
}

.highlight-card h3 {
    font-family: var(--font-heading);
    font-size: var(--font-size-2xl);
    color: var(--color-primary);
    margin-bottom: var(--spacing-sm);
    font-weight: 700;
}

.highlight-card p {
    color: var(--color-text-light);
    line-height: 1.6;
    font-size: var(--font-size-base);
}

/* ========================================
   ABOUT SECTION
   ======================================== */
.about {
    padding: var(--spacing-4xl) 5%;
    background: var(--gradient-bg);
}

.about-content {
    max-width: var(--container-md);
    margin: 0 auto;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-3xl);
    align-items: center;
}

.about-text h2 {
    font-family: var(--font-heading);
    font-size: var(--font-size-7xl);
    color: var(--color-primary);
    margin-bottom: var(--spacing-md);
    font-weight: 700;
}

.about-text p {
    line-height: 1.8;
    margin-bottom: var(--spacing-md);
    color: var(--color-text);
    font-size: var(--font-size-lg);
}

.about-stats {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: var(--spacing-lg);
}

.stat-box {
    background: var(--color-white);
    padding: var(--spacing-lg);
    border-radius: var(--radius-md);
    box-shadow: var(--shadow-md);
    text-align: center;
}

.stat-number {
    font-family: var(--font-heading);
    font-size: var(--font-size-8xl);
    font-weight: 800;
    background: linear-gradient(135deg, var(--color-primary), var(--color-primary-light));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.stat-label {
    color: var(--color-text-light);
    font-size: var(--font-size-base);
    margin-top: var(--spacing-xs);
}

/* ========================================
   SERVICES SECTION
   ======================================== */
.services {
    padding: var(--spacing-4xl) 5%;
    background: var(--color-bg);
}

.services-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: var(--spacing-xl);
    max-width: var(--container-md);
    margin: var(--spacing-2xl) auto 0;
}

.service-card {
    background: var(--gradient-service);
    padding: var(--spacing-2xl) var(--spacing-xl);
    border-radius: var(--radius-lg);
    color: var(--color-white);
    transition: var(--transition-base);
    position: relative;
    overflow: hidden;
}

.service-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: var(--gradient-service-overlay);
    opacity: 0;
    transition: var(--transition-base);
}

.service-card:hover::before {
    opacity: 1;
}

.service-card:hover {
    transform: translateY(-8px);
    box-shadow: var(--shadow-service);
}

.service-icon {
    font-size: var(--font-size-9xl);
    margin-bottom: var(--spacing-md);
    position: relative;
    z-index: 1;
}

.service-card h3 {
    font-family: var(--font-heading);
    font-size: var(--font-size-4xl);
    margin-bottom: var(--spacing-sm);
    font-weight: 700;
    position: relative;
    z-index: 1;
}

.service-card p {
    line-height: 1.7;
    opacity: 0.95;
    position: relative;
    z-index: 1;
}

/* ========================================
   PROJECTS SECTION
   ======================================== */
.projects {
    padding: var(--spacing-4xl) 5%;
    background: var(--color-bg);
}

.project-featured {
    max-width: var(--container-md);
    margin: var(--spacing-2xl) auto;
    background: var(--color-white);
    border-radius: var(--radius-lg);
    overflow: hidden;
    box-shadow: var(--shadow-lg);
}

.project-content {
    padding: var(--spacing-2xl);
}

.project-tag {
    display: inline-block;
    background: linear-gradient(135deg, var(--color-primary), var(--color-primary-light));
    color: var(--color-white);
    padding: var(--spacing-xs) var(--spacing-md);
    border-radius: var(--radius-xl);
    font-size: var(--font-size-xs);
    font-weight: 600;
    margin-bottom: var(--spacing-md);
}

.project-content h3 {
    font-family: var(--font-heading);
    font-size: var(--font-size-6xl);
    color: var(--color-navy);
    margin-bottom: var(--spacing-lg);
    font-weight: 700;
}

.project-details {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: var(--spacing-lg);
    margin-top: var(--spacing-lg);
}

.project-detail {
    padding: var(--spacing-md);
    background: var(--color-bg);
    border-radius: var(--radius-sm);
}

.project-detail h4 {
    font-family: var(--font-heading);
    color: var(--color-navy);
    font-size: var(--font-size-sm);
    margin-bottom: var(--spacing-xs);
    text-transform: uppercase;
    letter-spacing: 1px;
}

.project-detail p {
    color: var(--color-text);
    line-height: 1.6;
}

.result-highlight {
    background: var(--gradient-accent);
    color: var(--color-white);
    padding: var(--spacing-lg);
    border-radius: var(--radius-md);
    margin-top: var(--spacing-lg);
    text-align: center;
}

.result-highlight h4 {
    font-family: var(--font-heading);
    font-size: var(--font-size-5xl);
    margin-bottom: var(--spacing-xs);
}

/* ========================================
   CONTACT SECTION
   ======================================== */
.contact {
    padding: var(--spacing-4xl) 5%;
    background: var(--color-bg);
}

.contact-container {
    max-width: var(--container-md);
    margin: var(--spacing-2xl) auto 0;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-3xl);
}

.contact-info h3 {
    font-family: var(--font-heading);
    font-size: var(--font-size-5xl);
    color: var(--color-navy);
    margin-bottom: var(--spacing-lg);
    font-weight: 700;
}

.contact-item {
    display: flex;
    align-items: flex-start;
    gap: var(--spacing-md);
    margin-bottom: var(--spacing-lg);
}

.contact-icon {
    font-size: var(--font-size-5xl);
    color: var(--color-accent-orange);
}

.contact-item h4 {
    font-family: var(--font-heading);
    color: var(--color-navy);
    margin-bottom: var(--spacing-xs);
    font-weight: 600;
}

.contact-item p {
    color: var(--color-text-light);
    line-height: 1.6;
}

.contact-form {
    background: var(--color-white);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
}

.form-group {
    margin-bottom: var(--spacing-md);
}

.form-group label {
    display: block;
    font-weight: 600;
    color: var(--color-navy);
    margin-bottom: var(--spacing-xs);
    font-family: var(--font-heading);
}

.form-group input,
.form-group textarea {
    width: 100%;
    padding: var(--spacing-sm);
    border: 2px solid var(--color-accent-gold);
    border-radius: var(--radius-sm);
    font-family: var(--font-primary);
    font-size: var(--font-size-md);
    transition: var(--transition-base);
}

.form-group input:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--color-accent-orange);
}

.form-group textarea {
    resize: vertical;
    min-height: 150px;
}

.submit-button {
    width: 100%;
    padding: 1.2rem;
    background: var(--gradient-navy);
    color: var(--color-white);
    border: none;
    border-radius: var(--radius-sm);
    font-family: var(--font-heading);
    font-size: var(--font-size-xl);
    font-weight: 700;
    cursor: pointer;
    transition: var(--transition-base);
}

.submit-button:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-navy);
}

/* ========================================
   FOOTER
   ======================================== */
footer {
    background: var(--color-navy);
    color: var(--color-white);
    padding: var(--spacing-2xl) 5%;
    text-align: center;
}

.footer-content {
    max-width: var(--container-md);
    margin: 0 auto;
}

.footer-logos {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: var(--spacing-2xl);
    margin-bottom: var(--spacing-lg);
    flex-wrap: wrap;
}

.footer-logo {
    font-family: var(--font-heading);
    font-weight: 700;
    font-size: 1.2rem;
    opacity: 0.9;
}

.footer-logo-img {
    height: 50px;
    width: auto;
    object-fit: contain;
    filter: brightness(0) invert(1);
    opacity: 0.9;
}

.footer-partnership {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    flex-wrap: wrap;
    justify-content: center;
}

.certifications {
    display: flex;
    justify-content: center;
    gap: var(--spacing-lg);
    margin: var(--spacing-lg) 0;
    flex-wrap: wrap;
}

.cert-badge {
    background: rgba(255, 255, 255, 0.1);
    padding: var(--spacing-xs) var(--spacing-md);
    border-radius: var(--radius-xl);
    font-size: var(--font-size-sm);
    font-weight: 600;
}

.copyright {
    margin-top: var(--spacing-lg);
    opacity: 0.7;
    font-size: var(--font-size-sm);
}

/* ========================================
   FLOATING CONTACT BUTTON
   ======================================== */
.floating-contact {
    position: fixed;
    bottom: 30px;
    right: 30px;
    background: var(--gradient-navy);
    color: var(--color-white);
    padding: var(--spacing-sm) var(--spacing-lg);
    border-radius: var(--radius-full);
    box-shadow: 0 10px 30px rgba(10, 35, 66, 0.4);
    text-decoration: none;
    font-weight: 700;
    z-index: 999;
    transition: var(--transition-base);
    display: flex;
    align-items: center;
    gap: var(--spacing-xs);
}

.floating-contact:hover {
    transform: translateY(-3px);
    box-shadow: var(--shadow-orange);
}

/* ========================================
   INDUSTRIES SECTION
   ======================================== */
.industries {
    padding: var(--spacing-4xl) 5%;
    background: var(--gradient-industries);
}

.industry-card:hover {
    transform: translateY(-8px);
    box-shadow: var(--shadow-industry);
    border: 2px solid var(--color-accent-orange);
}

.industry-slide-card {
    min-width: 350px;
    margin-right: var(--spacing-lg);
    background: var(--color-white);
    border-radius: var(--radius-lg);
    padding: var(--spacing-xl) var(--spacing-lg);
    text-align: center;
    box-shadow: var(--shadow-card-alt);
    transition: var(--transition-base);
    cursor: pointer;
}

.industries-slider {
    position: relative;
    overflow: hidden;
    border-radius: var(--radius-md);
    margin-top: var(--spacing-2xl);
}

.industries-slider-container {
    display: flex;
    transition: var(--transition-transform);
}

.industries-slider-prev,
.industries-slider-next {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background: rgba(61, 44, 141, 0.9);
    color: var(--color-white);
    border: none;
    border-radius: var(--radius-circle);
    width: 50px;
    height: 50px;
    cursor: pointer;
    font-size: var(--font-size-4xl);
    z-index: 10;
    box-shadow: var(--shadow-button);
}

.industries-slider-prev {
    left: 15px;
}

.industries-slider-next {
    right: 15px;
}

/* ========================================
   SERVICES PAGE SPECIFIC STYLES
   ======================================== */
.services-page {
    background: var(--gradient-services-page);
}

.capability-section {
    margin-bottom: var(--spacing-3xl);
    background: var(--color-white);
    border-radius: var(--radius-lg);
    padding: var(--spacing-2xl);
    box-shadow: var(--shadow-xl);
    border-left: 6px solid var(--color-accent-orange);
}

.capability-slider {
    position: relative;
    overflow: hidden;
    border-radius: var(--radius-md);
}

.slider-container {
    display: flex;
    transition: var(--transition-transform);
}

.slide-card {
    min-width: 300px;
    margin-right: var(--spacing-lg);
    background: var(--color-bg-card);
    border-radius: var(--radius-md);
    padding: var(--spacing-lg);
    text-align: center;
    box-shadow: var(--shadow-card);
}

.slide-image {
    background: linear-gradient(135deg, var(--color-navy), var(--color-navy-light));
    border-radius: var(--radius-sm);
    height: 180px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--color-white);
    font-size: var(--font-size-md);
    text-align: center;
    margin-bottom: var(--spacing-md);
    border: 3px solid var(--color-accent-gold);
}

.slide-card h4 {
    font-family: var(--font-heading);
    color: var(--color-navy);
    font-size: var(--font-size-2xl);
    margin-bottom: 0.8rem;
    font-weight: 600;
}

.slide-card p {
    color: var(--color-text-light);
    line-height: 1.6;
    font-size: var(--font-size-base);
}

.slider-prev,
.slider-next {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background: rgba(10, 35, 66, 0.9);
    color: var(--color-white);
    border: none;
    border-radius: var(--radius-circle);
    width: 50px;
    height: 50px;
    cursor: pointer;
    font-size: var(--font-size-4xl);
    z-index: 10;
    box-shadow: var(--shadow-button);
}

.slider-prev {
    left: 15px;
}

.slider-next {
    right: 15px;
}

/* ========================================
   SCROLL ANIMATIONS
   ======================================== */
.fade-in {
    opacity: 0;
    transform: translateY(30px);
    transition: opacity 0.6s ease, transform 0.6s ease;
}

.fade-in.visible {
    opacity: 1;
    transform: translateY(0);
}

