.fs-lg {
    font-size: var(--font-size-lg);
}

.fs-xl {
    font-size: var(--font-size-xl);
}

.fs-2xl {
    font-size: var(--font-size-2xl);
}

.fs-3xl {
    font-size: var(--font-size-3xl);
}

.fs-4xl {
    font-size: var(--font-size-4xl);
}

.fs-5xl {
    font-size: var(--font-size-5xl);
}

.font-heading {
    font-family: var(--font-heading);
}

.font-italic {
    font-style: italic;
}

.rounded-sm {
    border-radius: var(--radius-sm);
}

.rounded-md {
    border-radius: var(--radius-md);
}

.rounded-lg {
    border-radius: var(--radius-lg);
}

.rounded-xl {
    border-radius: var(--radius-xl);
}

.rounded-full {
    border-radius: var(--radius-full);
}

.shadow-sm {
    box-shadow: var(--shadow-sm);
}

.shadow-md {
    box-shadow: var(--shadow-md);
}

.shadow-lg {
    box-shadow: var(--shadow-lg);
}

.shadow-card {
    box-shadow: var(--shadow-card);
}

.lh-base {
    line-height: 1.6;
}

.lh-relaxed {
    line-height: 1.7;
}

.lh-loose {
    line-height: 1.8;
}

.grid-2 {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
}

.grid-3 {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
}

.opacity-90 {
    opacity: 0.9;
}

.opacity-95 {
    opacity: 0.95;
}

.border-left-accent {
    border-left: 4px solid var(--color-accent-orange);
}

.border-left-primary {
    border-left: 4px solid var(--color-primary);
}

.border-left-blue {
    border-left: 4px solid var(--color-blue-light);
}

.border-left-gold {
    border-left: 4px solid var(--color-accent-gold);
}

/* Common Inline Style Patterns - Converted to Classes */
.hero-cta-container {
    display: flex;
    gap: var(--spacing-md);
    justify-content: center;
    flex-wrap: wrap;
}

.cta-compact {
    padding: var(--spacing-sm) var(--spacing-lg);
    font-size: var(--font-size-md);
}

.brand-philosophy {
    max-width: var(--container-sm);
    margin: 0 auto var(--spacing-3xl);
    text-align: center;
    padding: 0 var(--spacing-lg);
}

.philosophy-title {
    font-family: var(--font-heading);
    font-size: var(--font-size-5xl);
    color: var(--color-purple-alt);
    margin-bottom: var(--spacing-md);
    font-weight: 700;
}

.philosophy-quote {
    font-size: var(--font-size-3xl);
    font-style: italic;
    color: var(--color-blue-light);
    margin-bottom: var(--spacing-lg);
    font-weight: 600;
}

.philosophy-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: var(--spacing-lg);
    text-align: left;
    margin-bottom: var(--spacing-lg);
}

.philosophy-text {
    font-size: var(--font-size-xl);
    color: var(--color-text);
    line-height: 1.7;
}

.content-box {
    background: var(--color-white);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
    box-shadow: 0 10px 40px rgba(46, 0, 92, 0.1);
    margin-bottom: var(--spacing-2xl);
}

.vision-mission-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-lg);
}

.vision-card {
    background: var(--gradient-purple-alt);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
    color: var(--color-white);
}

.mission-card {
    background: var(--color-white);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
    box-shadow: 0 10px 40px rgba(46, 0, 92, 0.1);
}

.card-title {
    font-family: var(--font-heading);
    font-size: var(--font-size-5xl);
    margin-bottom: var(--spacing-md);
    font-weight: 700;
}

.card-title-navy {
    color: var(--color-navy);
}

.card-title-purple {
    color: var(--color-purple-alt);
}

.milestone-card {
    background: var(--color-white);
    padding: var(--spacing-xl);
    border-radius: var(--radius-md);
}

.milestone-current {
    background: var(--gradient-purple-alt);
    color: var(--color-white);
}

.milestone-badge {
    display: inline-block;
    padding: var(--spacing-xs) var(--spacing-sm);
    border-radius: var(--radius-xl);
    font-weight: 700;
    font-size: var(--font-size-sm);
}

.badge-current {
    background: rgba(255, 255, 255, 0.2);
    padding: 0.3rem 0.8rem;
    border-radius: 15px;
    font-size: 0.8rem;
    font-weight: 600;
}

.industry-icon {
    font-size: 4rem;
    margin-bottom: var(--spacing-md);
}

.industry-title {
    font-family: var(--font-heading);
    font-size: var(--font-size-3xl);
    color: var(--color-purple-light);
    margin-bottom: var(--spacing-sm);
    font-weight: 700;
}

.list-styled {
    line-height: 1.8;
    color: var(--color-text);
    font-size: var(--font-size-lg);
    padding-left: var(--spacing-md);
}

.list-styled li {
    margin-bottom: 0.8rem;
}

/* ========================================
   RESPONSIVE / MEDIA QUERIES
   ======================================== */
@media (max-width: 768px) {
    .nav-links {
        display: none;
    }

    .mobile-menu-toggle {
        display: flex;
    }

    .logo-img {
        height: 35px;
    }

    .logo-text {
        font-size: 1.2rem;
    }

    .footer-logo-img {
        height: 40px;
    }

    .hero-content h1 {
        font-size: var(--font-size-6xl);
    }

    .hero-content p {
        font-size: var(--font-size-md);
    }

    .highlights-grid,
    .services-grid,
    .project-details,
    .contact-container,
    .about-content,
    .vision-mission-grid,
    .philosophy-grid {
        grid-template-columns: 1fr;
    }

    .section-title {
        font-size: var(--font-size-5xl);
    }

    .about-stats {
        grid-template-columns: 1fr;
    }

    .slide-card {
        min-width: 280px;
    }

    .industry-slide-card {
        min-width: 300px;
    }

    .hero-cta-container {
        flex-direction: column;
        gap: var(--spacing-sm);
    }

    .cta-button {
        width: 100%;
        text-align: center;
    }
}

@media (max-width: 480px) {
    .nav-container {
        gap: var(--spacing-lg);
    }

    .hero-content h1 {
        font-size: var(--font-size-4xl);
    }

    .section-title {
        font-size: var(--font-size-4xl);
    }

    .card-title {
        font-size: var(--font-size-3xl);
    }

    .philosophy-title {
        font-size: var(--font-size-3xl);
    }

    .philosophy-quote {
        font-size: 1.2rem;
    }

    .industry-icon {
        font-size: 3rem;
    }
}

/* ========================================
   ADDITIONAL PAGE-SPECIFIC STYLES
   ======================================== */

/* Page Visibility */
.page-hidden {
    display: none;
}

/* Home Page - Brand Philosophy Section */
.philosophy-mission {
    font-size: var(--font-size-xl);
    color: var(--color-text);
    line-height: 1.7;
    text-align: center;
}

/* Industries Section */
.industries-section {
    padding: var(--spacing-4xl) 5%;
    background: var(--gradient-industries);
}

.industries-container {
    max-width: var(--container-lg);
    margin: 0 auto;
}

/* About Page Sections */
.about-section-wrapper {
    max-width: var(--container-md);
    margin: 0 auto var(--spacing-3xl);
    padding: 0 var(--spacing-lg);
}

.about-card {
    background: var(--color-white);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
    box-shadow: var(--shadow-lg);
    margin-bottom: var(--spacing-2xl);
}

.about-section-title {
    font-family: var(--font-heading);
    font-size: var(--font-size-5xl);
    color: var(--color-purple-alt);
    margin-bottom: var(--spacing-md);
    font-weight: 700;
}

.about-section-title-center {
    font-family: var(--font-heading);
    font-size: var(--font-size-5xl);
    color: var(--color-purple-alt);
    margin-bottom: var(--spacing-lg);
    font-weight: 700;
    text-align: center;
}

.about-subtitle {
    font-family: var(--font-heading);
    font-size: var(--font-size-3xl);
    color: var(--color-blue-light);
    margin-bottom: var(--spacing-md);
    font-weight: 600;
    font-style: italic;
}

.about-card p {
    line-height: 1.8;
    margin-bottom: var(--spacing-md);
    color: var(--color-text);
    font-size: var(--font-size-lg);
}

.about-card p:last-child {
    margin-bottom: 0;
}

/* Vision & Mission Cards */
.vision-card h3,
.mission-card h3 {
    font-family: var(--font-heading);
    font-size: var(--font-size-5xl);
    margin-bottom: var(--spacing-md);
    font-weight: 700;
}

.vision-card p {
    line-height: 1.8;
    font-size: var(--font-size-lg);
    opacity: 0.95;
}

.mission-card ul {
    line-height: 1.8;
    color: var(--color-text);
    font-size: var(--font-size-lg);
    padding-left: var(--spacing-md);
}

.mission-card ul li {
    margin-bottom: 0.8rem;
}

.mission-card h3 {
    color: var(--color-purple-alt);
}

/* Milestones Section */
.milestones-section {
    background: var(--gradient-industries);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
}

.milestones-grid {
    display: grid;
    gap: var(--spacing-lg);
}

.milestone-item {
    background: var(--color-white);
    padding: var(--spacing-xl);
    border-radius: var(--radius-md);
}

.milestone-item p {
    line-height: 1.7;
    color: var(--color-text);
    font-size: var(--font-size-lg);
}

.milestone-purple {
    border-left: 4px solid var(--color-purple-alt);
}

.milestone-blue {
    border-left: 4px solid var(--color-blue-light);
}

.milestone-gold {
    border-left: 4px solid var(--color-accent-gold);
}

.milestone-current {
    background: var(--gradient-purple-alt);
    color: var(--color-white);
    border-left: 4px solid var(--color-accent-orange);
}

.milestone-current p {
    color: var(--color-white);
    opacity: 0.95;
}

.milestone-header {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    margin-bottom: var(--spacing-sm);
}

.milestone-year {
    background: var(--gradient-purple-alt);
    color: var(--color-white);
    padding: 0.5rem var(--spacing-sm);
    border-radius: var(--radius-xl);
    font-weight: 700;
    font-size: var(--font-size-sm);
}

.milestone-current .milestone-year {
    background: var(--gradient-accent);
    color: var(--color-purple-alt);
}

/* Philosophy Section */
.philosophy-section {
    background: var(--gradient-industries);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
    text-align: center;
}

.philosophy-grid-about {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: var(--spacing-lg);
    text-align: left;
}

.philosophy-grid-about p {
    line-height: 1.8;
    color: var(--color-text);
    font-size: var(--font-size-lg);
}

/* Partnership Section */
.partnership-section {
    background: var(--color-white);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
    box-shadow: var(--shadow-lg);
}

.partnership-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-2xl);
    margin-bottom: var(--spacing-2xl);
}

.partnership-col h4 {
    font-family: var(--font-heading);
    color: var(--color-purple-alt);
    font-size: var(--font-size-2xl);
    margin-bottom: var(--spacing-sm);
    font-weight: 600;
}

.partnership-col ul {
    line-height: 1.8;
    color: var(--color-text);
    font-size: var(--font-size-lg);
    padding-left: var(--spacing-md);
}

.partnership-col ul li {
    margin-bottom: 0.5rem;
}

/* Facilities Section */
.facilities-section {
    background: var(--color-bg);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
}

.facilities-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-2xl);
}

.facility-card {
    background: var(--color-white);
    padding: var(--spacing-xl);
    border-radius: var(--radius-md);
}

.facility-title {
    font-family: var(--font-heading);
    color: var(--color-purple-alt);
    font-size: var(--font-size-2xl);
    margin-bottom: var(--spacing-sm);
    font-weight: 600;
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.facility-card p {
    line-height: 1.8;
    color: var(--color-text);
    font-size: var(--font-size-lg);
    margin-bottom: var(--spacing-sm);
}

.facility-card ul {
    line-height: 1.8;
    color: var(--color-text);
    font-size: var(--font-size-lg);
    padding-left: var(--spacing-md);
}

.facility-card ul li {
    margin-bottom: 0.5rem;
}

/* Certifications Section */
.certifications-section {
    background: var(--color-white);
    padding: var(--spacing-2xl);
    border-radius: var(--radius-lg);
    box-shadow: var(--shadow-lg);
    text-align: center;
}

.certifications-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-lg);
    text-align: left;
}

.certification-col h4 {
    font-family: var(--font-heading);
    color: var(--color-purple-alt);
    font-size: var(--font-size-xl);
    margin-bottom: var(--spacing-sm);
    font-weight: 600;
}

.certification-badges,
.certification-badges-alt {
    display: flex;
    flex-wrap: wrap;
    gap: 0.5rem;
}

.cert-badge {
    background: var(--gradient-purple-alt);
    color: var(--color-white);
    padding: 0.4rem var(--spacing-sm);
    border-radius: 15px;
    font-size: var(--font-size-xs);
    font-weight: 600;
}

.cert-badge-alt {
    background: var(--color-blue-light);
    color: var(--color-purple-alt);
    padding: 0.4rem var(--spacing-sm);
    border-radius: 15px;
    font-size: var(--font-size-xs);
    font-weight: 600;
}

/* Services Page Styles */
.services-header {
    text-align: center;
    margin-bottom: var(--spacing-4xl);
}

.services-main-title {
    font-family: var(--font-heading);
    font-size: var(--font-size-8xl);
    font-weight: 800;
    color: var(--color-white);
    margin-bottom: var(--spacing-sm);
    text-transform: uppercase;
    letter-spacing: 2px;
}

.title-divider {
    background: var(--gradient-accent);
    height: 4px;
    width: 120px;
    margin: var(--spacing-sm) auto var(--spacing-lg);
}

.services-philosophy {
    font-size: var(--font-size-4xl);
    font-style: italic;
    color: var(--color-accent-orange);
    font-weight: 600;
    font-family: var(--font-heading);
    margin-bottom: var(--spacing-md);
}

.services-description {
    font-size: var(--font-size-xl);
    color: var(--color-white);
    line-height: 1.7;
    max-width: 800px;
    margin: 0 auto;
}

.capabilities-container {
    max-width: var(--container-lg);
    margin: 0 auto;
}

.capability-header {
    margin-bottom: var(--spacing-2xl);
}

.capability-title {
    font-family: var(--font-heading);
    font-size: var(--font-size-6xl);
    color: var(--color-navy);
    margin-bottom: 0.5rem;
    font-weight: 700;
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
}

.capability-icon {
    fill: var(--color-accent-orange);
}

.capability-subtitle {
    color: var(--color-accent-gold);
    font-size: var(--font-size-xl);
    line-height: 1.6;
    font-weight: 600;
}

.capability-divider {
    height: 2px;
    background: linear-gradient(90deg, transparent 0%, var(--color-accent-gold) 50%, transparent 100%);
    margin: var(--spacing-2xl) 0;
}

.slide-image-content {
    text-align: center;
}

.slide-emoji {
    font-size: var(--font-size-8xl);
    margin-bottom: 0.5rem;
}

/* Projects Page Styles */
.projects-container {
    max-width: var(--container-md);
    margin: 0 auto;
}

.projects-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: var(--spacing-lg);
    margin-top: var(--spacing-2xl);
}

.project-card {
    background: var(--color-white);
    padding: var(--spacing-lg);
    border-radius: var(--radius-md);
    box-shadow: var(--shadow-md);
    transition: var(--transition-base);
}

.project-card:hover {
    transform: translateY(-5px);
    box-shadow: var(--shadow-lg);
}

.project-card-header {
    margin-bottom: var(--spacing-sm);
}

.project-type {
    display: inline-block;
    background: var(--gradient-cta);
    color: var(--color-primary);
    padding: 0.4rem var(--spacing-sm);
    border-radius: var(--radius-xl);
    font-size: var(--font-size-xs);
    font-weight: 600;
}

.project-card h4 {
    font-family: var(--font-heading);
    color: var(--color-navy);
    font-size: var(--font-size-xl);
    margin-bottom: var(--spacing-sm);
    font-weight: 700;
}

.project-card p {
    color: var(--color-text-light);
    line-height: 1.6;
    font-size: var(--font-size-base);
}

/* Contact Page Styles */
.contact-details {
    flex: 1;
}

/* Responsive Adjustments */
@media (max-width: 768px) {
    .philosophy-grid,
    .philosophy-grid-about,
    .partnership-grid,
    .facilities-grid,
    .certifications-grid {
        grid-template-columns: 1fr;
    }

    .projects-grid {
        grid-template-columns: 1fr;
    }

    .services-main-title {
        font-size: var(--font-size-6xl);
    }

    .services-philosophy {
        font-size: var(--font-size-2xl);
    }

    .capability-title {
        font-size: var(--font-size-4xl);
        flex-direction: column;
        align-items: flex-start;
    }
}
